import React, {useState} from 'react';
import './App.css'
import About from './components/About/About.js'
import Contact from './components/Contact/Contact.js'
import Realizations from './components/Realizations/Realizations.js'
import Services from './components/Services/Services.js'
function App() {

const [aboutOpen, setAboutOpen] = useState(false)

 const expandSection = (e) => {
   e.target.parentNode.parentNode.classList.add("expand")
   e.target.parentNode.parentNode.children[0].style.opacity = 1
   console.log()
  }

  const closeSection = (e) =>{
    e.target.parentNode.classList.remove("expand")
    e.target.parentNode.children[0].style.opacity = 0;
  }

  return (
<main className="main__wrapper">
  <section className="main__section">
    <div className="main__section--closeButton" onClick={(e)=>{closeSection(e), setAboutOpen(!aboutOpen)}}>&times;</div>
    <div className="main__section--box" onClick={wrapperFunction = (e) =>{(e)=>{expandSection(e), setAboutOpen(!aboutOpen)}}}>
      {aboutOpen ? <h1>About</h1>
        :<About/>}
      </div>
  </section>
  <section className="main__section">
    <div className="main__section--closeButton" onClick={(e)=>{closeSection(e)}}>&times;</div>
    <div className="main__section--box" onClick={(e)=>{expandSection(e)}}><Contact/></div>
  </section>
  <section className="main__section">
    <div className="main__section--closeButton" onClick={(e)=>{closeSection(e)}}>&times;</div>
    <div className="main__section--box" onClick={(e)=>{expandSection(e)}}><Realizations/></div>
  </section>
  <section className="main__section">
    <div className="main__section--closeButton" onClick={(e)=>{closeSection(e)}}>&times;</div>
    <div className="main__section--box" onClick={(e)=>{expandSection(e)}}><Services/></div>
  </section>
</main>
  );
}

export default App;
