import React, {useState} from 'react';
import './Navigation.css'
function Navigation() {

    //Scrolling stuff
$(window).scroll(function(){
    var scrollPosition = $(window).scrollTop();
    var one = $('#one').offset().top,
        two = $('#two').offset().top,
        three = $('#three').offset().top;
    var nav1 = $('a[href="#one"]'),
        nav2 = $('a[href="#two"]'),
        nav3 = $('a[href="#three"]');

    if(scrollPosition >= one) {
      $('.active').removeClass('active');
      $('#nav1').addClass('active');
      nav1.contents('li').text('Section 1');
      nav1.siblings().contents('li').text('');
    }
    if(scrollPosition >= two) {
      $('.active').removeClass('active');
      $('#nav2').addClass('active');
      nav2.contents('li').text('Section 2');
      nav2.siblings().contents('li').text('');
    }
     if(scrollPosition >= three) {
      $('.active').removeClass('active');
      $('#nav3').addClass('active');
      nav3.contents('li').text('Section 3');
      nav3.siblings().contents('li').text('');
    }
  });





  /*------------------------------------------------------
    smooth-scrolling; taken from https://css-tricks.com/snippets/jquery/smooth-scrolling/
  -------------------------------------------------------*/
    // Select all links with hashes
  $('a[href*="#"]')
    // Remove links that don't actually link to anything
    .not('[href="#"]')
    .not('[href="#0"]')
    .click(function(event) {
      // On-page links
      if (
        location.pathname.replace(/^\//, '') == this.pathname.replace(/^\//, '') 
        && 
        location.hostname == this.hostname
      ) {
        // Figure out element to scroll to
        var target = $(this.hash);
        target = target.length ? target : $('[name=' + this.hash.slice(1) + ']');
        // Does a scroll target exist?
        if (target.length) {
          // Only prevent default if animation is actually gonna happen
          event.preventDefault();
          $('html, body').animate({
            scrollTop: target.offset().top
          }, 1000, function() {
            // Callback after animation
            // Must change focus!
            var $target = $(target);
            $target.focus();
            if ($target.is(":focus")) { // Checking if the target was focused
              return false;
            } else {
              $target.attr('tabindex','-1'); // Adding tabindex for elements not focusable
              $target.focus(); // Set focus again
            };
          });
        }
      }
    });


  return (
      <>
      <nav class="nav">
  <ul>
    <a href="#one">
      <li class="active" id="nav1">
        Section 1
      </li>
    </a>
    <a href="#two">
      <li id="nav2">

        Section 2

      </li>
    </a>
    <a href="#three">
      <li id="nav3">

        Section 3

      </li>
    </a>
  </ul>
</nav>
<section id="one" class="section">
  <h1>Section 1</h1></section>
<section id="two" class="section">
  <h1>Section 2</h1></section>
<section id="three" class="section">
  <h1>Section 3</h1>
</section>
</>
  );
}

export default Navigation;
