import React, {useEffect, useState} from 'react';
import './Card.css'
import { gsap } from "gsap";

function Card({title, galleryImage, details}) {

const [slide, setSlide] = useState(0)

const nextSlide = () => {
  const slides = document.querySelectorAll(".slides__item")
  console.log(slide)
  gsap.fromTo(slides[slide], {opacity: 1}, {opacity: 0, duration: 2, delay: .5});
  setSlide(slide+1)
}

const prevSlide = () => {
  const slides = document.querySelectorAll(".slides__item")
  console.log(slide)
  gsap.fromTo(slides[slide], {right: "-100%"}, {right: 0, duration: 2, delay: .5});
  setSlide(slide-1)
}

  return (
      <section className="card">
        <div className="card__title">{title}</div>


        <div className="card__gallery">
          <div className="slides">
            <div className="slides__item">JEDEN</div>
            <div className="slides__item">DWA</div>
            <div className="slides__item">TRZY</div>
          </div>
          <div className="button">
            <button onClick={()=>{prevSlide()}}>prev</button>
            <button onClick={()=>{nextSlide()}}>next</button>
          </div>
        </div>


        <div className="card__opinion">3</div>
     </section>
  );
}

export default Card;
