import React, { useState, useRef, useEffect } from 'react';
import './Realizations.css'
import { gsap, Power1 } from "gsap";
import ScrollTrigger from 'gsap/ScrollTrigger'
import Slider from './Slider/Slider.js'
function Realizations() {

  let currentSlide = 2
  let zIndexMinus = -100
  let zIndexPlus = 10
  const sliderData = [
    {
      title: "title1",
      desc: "desc1",
      image: "https://upload.wikimedia.org/wikipedia/commons/9/95/Big_Pine_landscape.jpg",
    },
    {
      title: "title2",
      desc: "desc2",
      image:"https://images.unsplash.com/photo-1487958449943-2429e8be8625?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1050&q=80",
    },
    {
      title: "title3",
      desc: "desc3",
      image: "https://images.hdqwalls.com/download/sky-building-architecture-light-8k-d8-1920x1080.jpg"
    },
  ]
  gsap.registerPlugin(ScrollTrigger)
  useEffect(()=>{
    const currentText = document.querySelectorAll(".realization__currentTextWrapper")
    console.log(currentText[0].childNodes[1])
    gsap.set([currentText[2],currentText[1]], {zIndex: -10});
    const currentImageWrapper = document.querySelector(".realization__currentImageWrapper")

    const realizationNextContainer = document.querySelector('.realization__next');
    const tl = gsap.timeline({
      scrollTrigger:{
        trigger: '#realizations',
        start: "top 40%"
      }
    })

     tl.from("#cover", 1, { scaleX: 0, transformOrigin: "left" });
     tl.fromTo(currentImageWrapper.childNodes[0].childNodes,{opacity:0},{opacity:1, duration:1})
     tl.to("#cover", 1, { scaleX: 0, transformOrigin: "right" }, "reveal");
     tl.to(currentImageWrapper.childNodes[2], {zIndex:9})
     tl.fromTo(currentText[0].childNodes[0], {y: '+=50', opacity:'0'}, {y:0, opacity:1, duration:1, ease: 'easeInOut'})
     tl.fromTo(currentText[0].childNodes[1], {x: '+=50', opacity:'0'}, {x:0, opacity:1, duration:1, ease: 'easeInOut'})
     tl.from(realizationNextContainer.childNodes, {opacity:0, scale:.8, stagger:.5, ease: 'easeInOut'})
  })

  const nextSlide = () =>{
    const currentImageWrapper = document.querySelector(".realization__currentImageWrapper").childNodes[0].childNodes
    const currentTextWrapper = document.querySelector(".realization__currentText").childNodes

    const nextImageWrapper = document.querySelector(".realization__nextImageWrapper").childNodes[0].childNodes
    const nextTextWrapper = document.querySelector(".realization__nextText").childNodes

    console.log(currentTextWrapper)
    if(currentSlide > 0){
      console.log(currentSlide)
      gsap.to(currentImageWrapper[currentSlide], {zIndex: zIndexMinus})
      gsap.to(currentTextWrapper[currentSlide], {zIndex: zIndexMinus})

      gsap.to(nextImageWrapper[currentSlide], {zIndex: zIndexMinus})
      gsap.to(nextTextWrapper[currentSlide], {zIndex: zIndexMinus})

    zIndexMinus-=1
    currentSlide-=1
  }}

  const prevSlide = () => {
    const currentImageWrapper = document.querySelector(".realization__currentImageWrapper").childNodes[0].childNodes
    const currentTextWrapper = document.querySelector(".realization__currentText").childNodes

    const nextImageWrapper = document.querySelector(".realization__nextImageWrapper").childNodes[0].childNodes
    const nextTextWrapper = document.querySelector(".realization__nextText").childNodes
    if(currentSlide <= currentImageWrapper.length-2){
      console.log(currentSlide)

      gsap.to(currentImageWrapper[currentSlide], {zIndex: zIndexPlus})
      gsap.to(currentTextWrapper[currentSlide], {zIndex: zIndexPlus})

      gsap.to(nextImageWrapper[currentSlide], {zIndex: zIndexMinus})
      gsap.to(nextTextWrapper[currentSlide], {zIndex: zIndexMinus})

    zIndexPlus+=1
    currentSlide+=1
  }
}


const buttonsValue =[">", "<"]
  return (
      <section className="realization"  id="realizations">

        <div className="realization__current">
          <div className="realization__currentText">
          {sliderData.map((slide)=>(
            <span className="realization__currentTextWrapper">
            <h1 className="realization__currentTextTitle">{slide.title}</h1>
            <h3 className="realization__currentTextBody">{slide.desc}</h3>
            </span>
            ))}
          </div>
           <div className="realization__currentImageWrapper" >
            <div className="realization__currentImage">

              {sliderData.map((slide)=>(
                <img className="slide" src={slide.image}/>
              ))}
            <img src="https://www.levistrauss.com/wp-content/uploads/2020/05/Black_Box.png" className="slide" id="cover"/>
            </div>
           </div> 
        </div>

        <div className="realization__next">
          {/* <div className="realization__nextImage">THERE WILL BE NEXT IMAGE</div> */}
          <div className="realization__nextImageWrapper" >
          <div className="realization__nextImage">  
          {sliderData.map((slide)=>(
           <img src={slide.image}/>
          ))}
           </div>
          </div>
            <div className="realization__nextText">
              {sliderData.map((slide)=>(
              <div className="realization__nextTextWrapper">
                <h3  className="realization__nextTextTitle">NEXT PROJECT</h3>
                <h3  className="realization__nextTextBody">{slide.title}</h3>
              </div>
              ))}
            </div>

          <div className="realization__nextButtons">
            <div className="realization__nextButton realization__nextButton--next" id="nextButtonSlide" onClick={()=>{nextSlide()}}>
              <span className="realization__nextButtonArrow"  >{buttonsValue[0]}</span></div>
            <div className="realization__nextButton realization__nextButton--prev" id="prevButtonSlide" onClick={()=>{prevSlide()}}>
              <span className="realization__nextButtonArrow" >{buttonsValue[1]}</span></div>
          </div>
        </div>

     </section>
  );
}

export default Realizations;
