console.log(paragraphs.childNodee)

gsap.fromTo(paragraphs.children, {y: '-=100', opacity: 0}, {y: 0, opacity: 1, stagger: .5, duration: 2, ScrollTrigger:{
trigger: paragraphs,
start: 'top top'
}})

function AboutNext() {

    return (
    <section className="aboutNext slide" id="aboutNext">
    <div className="aboutNext__textWrapper"> 
      <p>Jesteśmy grupą ludzi która</p>
      <p><span>buduje</span> domy z pasją i poświeceniem.</p>
      <p>Zbudujemy rowniez Twoj</p>
      <p><span>wymarzony dom!</span></p>
      </div>
    </section>
  );
}

export default AboutNext;
