import React, {useEffect, useState} from 'react';
import './Card.css'
import { gsap } from "gsap";

function Card({title, galleryImage, details}) {

const [slide, setSlide] = useState([])

useEffect(() => {
  const slides = document.querySelectorAll(".slides__item")
  console.log(typeof(slides))
  let helperArray = [];
  Object.keys(slides).map(function(key, index) {
    helperArray.push(slides[index])
  });
  setSlide(helperArray)
}, [])
console.log(slide)

const nextSlide = () => {
  const slides = document.querySelectorAll(".slides__item")
  console.log(slides[slide])
  gsap.fromTo(slides[slide], {opacity: 1}, {opacity: 0, duration: 1, delay: .2});
  setSlide(slide+1)
}

const prevSlide = () => {
  const slides = document.querySelectorAll(".slides__item")
  console.log(slides[slide])
  gsap.fromTo(slides[slide], {opacity: 0}, {opacity: 1, duration: 1, delay: .2});
  setSlide(slide-1)
}

  return (
      <section className="card">
        <div className="card__title">{title}</div>


        <div className="card__gallery">
          <div className="slides">
            <div className="slides__item">JEDEN</div>
            <div className="slides__item">DWA</div>
            <div className="slides__item">TRZY</div>
          </div>
          <div className="button">
            <button onClick={()=>{prevSlide()}}>prev</button>
            <button onClick={()=>{nextSlide()}}>next</button>
          </div>
        </div>


        <div className="card__opinion">3</div>
     </section>
  );
}

export default Card;
