  //    tl.from("#cover", 1, { scaleX: 0, transformOrigin: "left" });
  //    tl.fromTo(currentImageWrapper.childNodes[0].childNodes,{opacity:0},{opacity:1, duration:1})
  //    tl.to("#cover", 1, { scaleX: 0, transformOrigin: "right" }, "reveal");
  //    tl.fromTo(currentText[0].childNodes[0], {y: '+=50', opacity:'0'}, {y:0, opacity:1, duration:1, ease: 'easeInOut'})
