import React, {useState, useEffect, useRef} from 'react';
import './Navigation.css'
import logo from '../static/logo.png'
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";



function Navigation() {


  gsap.registerPlugin(ScrollTrigger);
  function goToSection(i, anim) {
    gsap.to(window, {
      //scrollTo: {y: i*innerHeight, autoKill: false},
      duration: 1
    });

    if(anim) {
      anim.restart();
    }
  }

  gsap.utils.toArray("section").forEach((panel, i) => {
    ScrollTrigger.create({
      trigger: panel,
      onEnter: () => goToSection(i)
    });

    ScrollTrigger.create({
      trigger: panel,
      start: "bottom bottom",
      onEnterBack: () => goToSection(i),
    });
  });



  let sections = gsap.utils.toArray(".rows");
  gsap.to(sections, {
    xPercent: -100 * (sections.length - 1),
    ease: "none",
    scrollTrigger: {
      trigger: ".pinme",
      pin: true,
      scrub: 1,
     // snap: 1 / (sections.length - 1),
      // base vertical scrolling on how wide the container is so it feels more natural.
      end: () => "+=" + document.querySelector(".pinme").offsetWidth
    }
  });



const [activeDot, setActiveDot] = useState("about");

  return (
<section className="navigation__wrapper">
{/* <a href="/" className="navigation__logo">
<img src={logo}/>
  <h2>YOURNAME</h2></a> */}

<ul className="navigation__list">
<li onClick={()=>setActiveDot("about")}><a href="#about" data-scroll="about"><span>O Nas</span><div className="dot"/></a></li>
<li onClick={()=>setActiveDot("services")}><a href="#services" data-scroll="services"><span>Us≈Çugi</span><div className="dot"/></a></li>
<li onClick={()=>setActiveDot("realizations")}><a href="#realizations" data-scroll="realizations"><span>Realizacje</span><div className="dot-active"/></a></li>
<li onClick={()=>setActiveDot("contact")}><a href="#contact" data-scroll="contact"><span>Kontakt</span><div className="dot"/></a></li>
</ul>
</section> 

  );
}

export default Navigation;
