import React, {useState} from 'react';
import './App.css'
import About from './components/About/About.js'
import Contact from './components/Contact/Contact.js'
import Realizations from './components/Realizations/Realizations.js'
import Services from './components/Services/Services.js'
function App() {

const [aboutOpen, setAboutOpen] = useState(false)
const [contactOpen, setContactOpen] = useState(false)
const [realizationsOpen, setRealizationsOpen] = useState(false)
const [servicesOpen, setServicesOpen] = useState(false)

 const expandSection = (e, typeOfBox) => {
   e.target.parentNode.parentNode.classList.add("expand")
   e.target.parentNode.parentNode.children[0].style.opacity = 1
   switch(typeOfBox){
    case 'about':
     setAboutOpen(true)
    break;
    case 'contact':
     setContactOpen(true)
    break;
    case 'realizations':
     setRealizationsOpen(true)
    break;
    case 'Services':
     setServicesOpen(true)
    break;
  }
  }

  const closeSection = (e, typeOfBox) =>{
    e.target.parentNode.classList.remove("expand")
    e.target.parentNode.children[0].style.opacity = 0;
    switch(typeOfBox){
      case 'about':
       setAboutOpen(false)
      break;
      case 'contact':
       setContactOpen(false)
      break;
      case 'realizations':
       setRealizationsOpen(false)
      break;
      case 'Services':
       setServicesOpen(false)
      break;
    }
  }

  return (
<main className="main__wrapper">
  <section className="main__section">
    <div className="main__section--closeButton" onClick={(e)=>{closeSection(e, "about")}}>&times;</div>
    <div className="main__section--box" onClick={(e)=>{expandSection(e, "about")}}>
      {aboutOpen ? <h1>About</h1>
        :<About/>}
      </div>
  </section>
  <section className="main__section">
    <div className="main__section--closeButton" onClick={(e)=>{closeSection(e, "contact")}}>&times;</div>
    <div className="main__section--box" onClick={(e)=>{expandSection(e, "contact")}}>
      {contactOpen ? <h1>Contact</h1>
        :<Contact/>}
      </div>
  </section>
  <section className="main__section">
    <div className="main__section--closeButton" onClick={(e)=>{closeSection(e, "realizations")}}>&times;</div>
    <div className="main__section--box" onClick={(e)=>{expandSection(e, "realizations")}}>      
      {realizationsOpen ? <h1>Realizations</h1>
        :<Realizations/>}
      </div>
  </section>
  <section className="main__section">
    <div className="main__section--closeButton" onClick={(e)=>{closeSection(e, "services")}}>&times;</div>
    <div className="main__section--box" onClick={(e)=>{expandSection(e, "services")}}>      
      {servicesOpen ? <h1>Services</h1>
        :<Services/>}
      </div>
  </section>
</main>
  );
}

export default App;
