import React, {useState, useEffect, useRef} from 'react';
import './Navigation.css'
import logo from '../static/logo.png'
import { gsap } from "gsap";
import { TweenLite } from "gsap";



function Navigation() {

/*slideshow*/

var imgs = document.querySelectorAll('#mainimgcontainer img');

// Create the slideshow timeline
var tl = gsap.timeline({paused: true, repeat: -1});
imgs.forEach(function(img, i) {
  var lastI = i === 0 ? imgs.length - 1 : i;

  tl.to(imgs[lastI], { duration: 1, autoAlpha: 0, scale: 1.1 }, "+=4");
  tl.to(imgs[i], { duration: 1.3, autoAlpha: 1, scale: 1 }, "<");
});

gsap.set("#mainimgcontainer", { opacity: 0, yPercent: -10 });

/*fullpage*/
gsap('#fullpage').fullpage({
  autoScrolling: true,
  navigation: true,
  scrollingSpeed: 700,
  onLeave: (origin, destination, direction) => {
    if (destination === 2) {
      gsap.to("#mainimgcontainer", { opacity: 1, yPercent: 0, delay: 0.6 });
      tl.play();
    } else {
      gsap.to("#mainimgcontainer", { opacity: 0, yPercent: -10 });
      tl.pause();
    }
  }
}); 



const [activeDot, setActiveDot] = useState("about");

  return (
<section className="navigation__wrapper">
{/* <a href="/" className="navigation__logo">
<img src={logo}/>
  <h2>YOURNAME</h2></a> */}

<ul className="navigation__list">
<li onClick={()=>setActiveDot("about")}><a href="#about" ><span>O Nas</span><div className="dot"/></a></li>
<li onClick={()=>setActiveDot("services")}><a href="#services"><span>Usługi</span><div className="dot"/></a></li>
<li onClick={()=>setActiveDot("realizations")}><a href="#realizations"><span>Realizacje</span><div className="dot-active"/></a></li>
<li onClick={()=>setActiveDot("contact")}><a href="#contact"><span>Kontakt</span><div className="dot"/></a></li>
</ul>
</section> 

  );
}

export default Navigation;
