import React, { useState, useRef, useCallback, useEffect } from 'react';
import './test.css'
import { gsap, Power1 } from "gsap";

export default function test() {



useEffect(()=>{
    var slideDelay = 1.5;
    var slideDuration = 99999.3;
    var snapX;

    var slides = document.querySelectorAll(".slide");
    var prevButton = document.querySelector("#prevButton");
    var nextButton = document.querySelector("#nextButton");
    var progressWrap = gsap.utils.wrap(0, 1);

    var numSlides = slides.length;

    gsap.set(slides, {
      backgroundColor: "random([red, blue, green, purple, orange, yellow, lime, pink])",
      xPercent: i => i * 100
    });

    var wrap = gsap.utils.wrap(-100, (numSlides - 1) * 100);
    var timer = gsap.delayedCall(slideDelay);

    var animation = gsap.to(slides, {
      xPercent: "+=" + (numSlides * 100),
      duration: 1,
      ease: "none",
      paused: true,
      repeat: -1,
      modifiers: {
        xPercent: wrap
      }
    });

    var proxy = document.createElement("div");
    var slideAnimation = gsap.to({}, {});
    var slideWidth = 0;
    var wrapWidth = 0;
    resize();


    window.addEventListener("resize", resize);

    prevButton.addEventListener("click", function() {
      animateSlides(1);
    });

    nextButton.addEventListener("click", function() {
      animateSlides(-1);
    });

    function updateDraggable() {
      timer.restart(true);
      slideAnimation.kill();
      this.update();
    }

    function animateSlides(direction) {

      timer.restart(true);
      slideAnimation.kill();

      var x = snapX(gsap.getProperty(proxy, "x") + direction * slideWidth);

      slideAnimation = gsap.to(proxy, {
        x: x,
        duration: slideDuration,
        onUpdate: updateProgress
      });  
    }



    function updateProgress() { 
      animation.progress(progressWrap(gsap.getProperty(proxy, "x") / wrapWidth));
    }

    function resize() {

      var norm = (gsap.getProperty(proxy, "x") / wrapWidth) || 0;

      slideWidth = slides[0].offsetWidth;
      wrapWidth = slideWidth * numSlides;
      snapX = gsap.utils.snap(slideWidth);

      gsap.set(proxy, {
        x: norm * wrapWidth
      });

      animateSlides(0);
      slideAnimation.progress(1);
    }
    })




    return (
        <div>
            <main>

   <div class="slides-container">
     <div class="slides-inner">
       <div class="slide">1</div>
       <div class="slide">2</div>
       <div class="slide">3</div>
       <div class="slide">4</div>
       <div class="slide">5</div>
       <div class="slide">6</div>
       <div class="slide">7</div>
       <div class="slide">8</div>
       <div class="slide">9</div>
       <div class="slide">10</div>
     </div>
   </div>

   <div class="controls">
     <button id="prevButton">Prev</button>
     <button id="nextButton">Next</button>
   </div>

 </main>
        </div>
    )
}
