import React, {useState, useEffect, useRef} from 'react';
import './Navigation.css'
import logo from '../static/logo.png'
import { gsap } from "gsap";
import { TweenLite } from "gsap";



function Navigation() {

const slides = document.querySelectorAll(".panel");
const container = document.querySelector(".main__wrapper");
let dots = document.querySelectorAll("li");
let oldSlide = 0;
let activeSlide = 0;
let navDots = [];
let dur = 0.6;
let toolTipAnims = [];
let ih = window.innerHeight;

const mouseAnim = gsap.timeline({repeat:-1, repeatDelay:1});
const handAnim = gsap.timeline({repeat:-1, repeatDelay:1});
const cursorAnim = gsap.timeline({repeat:-1, repeatDelay:1});
const arrowAnim = gsap.timeline({repeat:-1, repeatDelay:1});






function slideAnim(e) {

  oldSlide = activeSlide;
  // dragging the panels
  if (this.id === "dragger") {
    activeSlide = offsets.indexOf(this.endY);
  } else {
    if (gsap.isTweening(container)) {
      return;
    }
    // up/down arrow clicks
    if (this.id === "downArrow" || this.id === "upArrow") {
      activeSlide = this.id === "downArrow" ? (activeSlide += 1) : (activeSlide -= 1);
      // click on a dot
    } else if (this.className === "dot") {
      activeSlide = this.index;
      // scrollwheel
    } else {
      activeSlide = e.deltaY > 0 ? (activeSlide += 1) : (activeSlide -= 1);
    }
  }
  // make sure we're not past the end or beginning slide
  activeSlide = activeSlide < 0 ? 0 : activeSlide;
  activeSlide = activeSlide > slides.length - 1 ? slides.length - 1 : activeSlide;
  if (oldSlide === activeSlide) {
    return;
  }
  // if we're dragging we don't animate the container
  if (this.id != "dragger") {
    gsap.to(container, dur, { y: offsets[activeSlide], ease:"power2.inOut", onUpdate:tweenDot });
  }
}

gsap.set(".hideMe", {opacity:1});
window.addEventListener("wheel", slideAnim);
window.addEventListener("resize", newSize);

// make the container a draggable element
  let dragMe = Draggable.create(container, {
  type: "y",
  edgeResistance: 1,
  onDragEnd: slideAnim,
  onDrag: tweenDot,
  onThrowUpdate: tweenDot,
  snap: offsets,
  inertia:true,
  zIndexBoost: false,
  allowNativeTouchScrolling: false,
  bounds: "#masterWrap"
});

dragMe[0].id = "dragger";
newSize();

// resize all panels and refigure draggable snap array
function newSize() {
  offsets = [];
  ih = window.innerHeight;
  gsap.set("#panelWrap", { height: slides.length * ih });
  gsap.set(slides, { height: ih });
  for (let i = 0; i < slides.length; i++) {
    offsets.push(-slides[i].offsetTop);
  }
  gsap.set(container, { y: offsets[activeSlide] });
  dragMe[0].vars.snap = offsets;
}

// tween the dot animation as the draggable moves
  function tweenDot() {
    gsap.set(dotAnim, {
    time: Math.abs(gsap.getProperty(container, "y") / ih) + 1
  });
}








const [activeDot, setActiveDot] = useState("about");

  return (
<section className="navigation__wrapper">
{/* <a href="/" className="navigation__logo">
<img src={logo}/>
  <h2>YOURNAME</h2></a> */}

<ul className="navigation__list">
<li onClick={()=>setActiveDot("about")}><a href="#about" ><span>O Nas</span><div className="dot"/></a></li>
<li onClick={()=>setActiveDot("services")}><a href="#services"><span>Us≈Çugi</span><div className="dot"/></a></li>
<li onClick={()=>setActiveDot("realizations")}><a href="#realizations"><span>Realizacje</span><div className="dot-active"/></a></li>
<li onClick={()=>setActiveDot("contact")}><a href="#contact"><span>Kontakt</span><div className="dot"/></a></li>
</ul>
</section> 

  );
}

export default Navigation;
