import React, {useState} from 'react';
import './App.css'
import About from './components/About/About.js'
function App() {

  var Boxlayout = (function () {
    var wrapper = document.body,
      sections = Array.from(document.querySelectorAll(".main__section")),
      closeButtons = Array.from(document.querySelectorAll(".main__section--closeButton")),
      expandedClass = "is-expanded",
      hasExpandedClass = "has-expanded-item";

    return { init: init };

    function init() {
      _initEvents();
    }

    function _initEvents() {
      sections.forEach(function (element) {
        element.onclick = function () {
          _openSection(this);
        };
      });
      closeButtons.forEach(function (element) {
        element.onclick = function (element) {
          element.stopPropagation();
          _closeSection(this.parentElement);
        };
      });
    }

    function _openSection(element) {
      if (!element.classList.contains(expandedClass)) {
        element.classList.add(expandedClass);
        wrapper.classList.add(hasExpandedClass);
      }
    }

    function _closeSection(element) {
      if (element.classList.contains(expandedClass)) {
        element.classList.remove(expandedClass);
        wrapper.classList.remove(hasExpandedClass);
      }
    }
  })();

  Boxlayout.init();


  return (
<main class="main__wrapper">
  <section class="main__section">
    <div class="main__section--closeButton">&times;</div>
    <div class="main__section--box">123</div>
  </section>
  <section class="main__section">
    <div class="main__section--closeButton">&times;</div>
    <div class="main__section--box">Section 2</div>
  </section>
  <section class="main__section">
    <div class="main__section--closeButton">&times;</div>
    <div class="main__section--box">Section 3</div>
  </section>
  <section class="main__section">
    <div class="main__section--closeButton">&times;</div>
    <div class="main__section--box">Section 4</div>
  </section>
</main>
  );
}

export default App;
