import React, {useState, useEffect, useRef} from 'react';
import './Navigation.css'
import logo from '../static/logo.png'
//import { gsap } from "gsap";
import { TweenLite } from "gsap";



function Navigation() {

/*slideshow*/

var imgs = $.makeArray($('#mainimgcontainer img'));
        imgs.reverse();
        function crossfade() {
            gsap.to(imgs[0], 1, { autoAlpha: 0, scale: 1.1 });
            gsap.to(imgs[1], 1.3, { autoAlpha: 1, scale: 1 });
            imgs.push(imgs.shift())
        }
        var cycle = setInterval(crossfade, 4000);

/*fullpage*/

$('#fullpage').fullpage({
            autoScrolling: true,
            navigation: true,
            scrollingSpeed: 700,

            onLeave: (origin, destination, direction) => {


                if (destination.index === 1 && direction == 'down') {
                    gsap.fromTo("#mainimgcontainer", 0.5, { opacity: "0", y: "10%", }, { opacity: "1", y: "0", delay: 0.6 });

                }

                else if (destination.index === 1 && direction == 'up') {
                    gsap.fromTo("#mainimgcontainer", 0.5, { opacity: "0", y: "-10%" }, { opacity: "1", y: "0", delay: 0.6 });
                } 
            }
            }); 



const [activeDot, setActiveDot] = useState("about");

  return (
<section className="navigation__wrapper">
{/* <a href="/" className="navigation__logo">
<img src={logo}/>
  <h2>YOURNAME</h2></a> */}

<ul className="navigation__list">
<li onClick={()=>setActiveDot("about")}><a href="#about" ><span>O Nas</span><div className="dot"/></a></li>
<li onClick={()=>setActiveDot("services")}><a href="#services"><span>Usługi</span><div className="dot"/></a></li>
<li onClick={()=>setActiveDot("realizations")}><a href="#realizations"><span>Realizacje</span><div className="dot-active"/></a></li>
<li onClick={()=>setActiveDot("contact")}><a href="#contact"><span>Kontakt</span><div className="dot"/></a></li>
</ul>
</section> 

  );
}

export default Navigation;
