      scrollTrigger: {
        trigger: panel,
        start: "40% 50%", // position of trigger meets the scroller position
      }
    });

    tl.from(panel.querySelectorAll('.headline .char'), {
      x: -20, 
      stagger: 0.02,
      opacity: 0
    })
  })
}

function init() {
  textanimation();
}

