import React, { useRef, useEffect, useState } from 'react';
import './AboutNext.css'

import { gsap } from "gsap";
import ScrollTrigger from 'gsap/ScrollTrigger'




function AboutNext() {  
  const content = gsap.utils.toArray('.content');
  const image = gsap.utils.toArray('.image');
  const heading = gsap.utils.toArray('.heading');
  const text = gsap.utils.toArray('.text');

  // IE11対策
  const contentArray = Array.prototype.slice.call(content,0); 

  contentArray.forEach((elem, index) => {
    gsap.timeline({
      default: { ease: 'power2.easeOut', duration: 1 },
      scrollTrigger: {
        markers: true, // マーカー表示（開発用）
        trigger: elem, // この要素と交差するとイベントが発火
        start: 'top 50%', // ウィンドウのどの位置を発火の基準点にするか
        end: 'bottom top', // ウィンドウのどの位置をイベントの終了点にするか
        toggleActions: 'play none none none', // スクロールイベントで発火するアニメーションの種類
      }
    })
    .fromTo(heading[index], { opacity: 0, y: 10 }, { opacity: 1, y: 0 }, '-=1')
    .fromTo(text[index], { opacity: 0, y: 10 }, { opacity: 1, y: 0 })
    .fromTo(image[index],{ opacity: 0, x: -10 }, { opacity: 1, x: 0 }, '-=1')
  });

      return (
 <>
 <div class="container">
  <h1 class="title">GSAP ScrollTrigger</h1>
  <p class="caption">please scrolldown</p>
  <div class="content">
    <img class="image" src="https://picsum.photos/id/683/300/400"/>
    <div class="content-inner">
      <h2 class="heading">そしてよだかの星は燃えつづけました。</h2>
      <p class="text">
        もうよだかは落ちているのか、のぼっているのか、さかさになっているのか、上を向いているのかも、わかりませんでした。ただこころもちはやすらかに、その血のついた大きなくちばしは、横にまがっては居ましたが、たしかに少しわらって居おりました。<br/>
        <br/>よだかの星より
      </p>
    </div>
  </div>
  <div class="content">
    <img class="image" src="https://picsum.photos/id/593/300/400"/>
    <div class="content-inner">
      <h2 class="heading">「その声は、我が友、李徴子ではないか？」</h2>
      <p class="text">
        袁は李徴と同年に進士の第に登り、友人の少かった李徴にとっては、最も親しい友であった。温和な袁の性格が、峻峭な李徴の性情と衝突しなかったためであろう。<br/>
        <br/>山月記より
      </p>
    </div>
  </div>
</div>
 </>
  );
}

export default AboutNext;
