import React, { useState, useRef, useCallback, useEffect } from 'react';
import './Slider.css'
import { gsap, Power1 } from "gsap";


export default function Slider({sliderImageURL}) {




useEffect(()=>{
gsap.set('.motiv img',{xPercent:-50, yPercent:-50})

const imgs = gsap.utils.toArray("#imgContainer .motiv");
const fade = 1.5; // fade time 

gsap.set(imgs[0], {autoAlpha:1})

const crossfade = () => {
  const action = gsap.timeline()
  .to(imgs[0], {autoAlpha:0, duration:fade})
  .to(imgs[1], {autoAlpha:1, duration:fade},0)
  imgs.push( imgs.shift() );
}  
gsap.delayedCall(next, crossfade);

})



    return (

  <main>
     <div className="slides-container">
       <div className="slides-inner">


       <div id="imgContainer">

<div class="motiv motiv01"> 
  <img src='https://unsplash.it/1000/450?image=1080'/>
  <h1>ONE</h1> 
</div>

<div class="motiv motiv02"> 
  <img src='https://unsplash.it/1000/450?image=889'/>
  <h1>TWO</h1> 
</div>

<div class="motiv motiv03"> 
  <img src='https://unsplash.it/1000/450?image=766'/>
  <h1>THREE</h1> 
</div> 
</div>




      </div>
   </div>
 </main>
    )
}
