import React, { useRef, useEffect, useState } from 'react';
import './AboutNext.css'

import { gsap } from "gsap";
import ScrollTrigger from 'gsap/ScrollTrigger'




function AboutNext() {  
  useEffect(()=>{
    gsap.registerPlugin(ScrollTrigger);

let rule = CSSRulePlugin.getRule(".sec1 .photoFrame:after");

var tl = gsap.timeline({
  scrollTrigger: {
    trigger: ".photoFrame",
    start: "top-=40px center+=100px",
    end: "center center",
    // scrub: true,
    toggleActions: "play none none none"
  }
});

tl.to(rule, {
  duration: 0.65,
  width: "0%",
  ease: "power2.out"
}).from(".image", {
  scale: 1.2,
  duration: 0.65,
  delay: -0.65,
  ease: "power2.out"
});

  },[])

