 <>
 <section class='hero-section'>
  <span class='heading'>GSAP scroll trigger</span>
</section>
<section class='sec1'>
  <div class="photoFrame">
    <img class='image' src='https://images.pexels.com/photos/5781095/pexels-photo-5781095.jpeg?cs=tinysrgb&dpr=2&h=650&w=940' />
  </div>
</section>
 </>
  );
}

export default AboutNext;
