import React, {useState, useEffect, useRef} from 'react';
import './Navigation.css'
import logo from '../static/logo.png'
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";



function Navigation() {

  gsap.registerPlugin(ScrollTrigger);

gsap.utils.toArray("#about","services").forEach((panel, i) => {
  ScrollTrigger.create({
    trigger: panel,
    start: "top top", 
    pin: true, 
    pinSpacing: false 
  });
});


ScrollTrigger.create({
  snap: 1 / 4 // snap whole page to the closest section!
});



const [activeDot, setActiveDot] = useState("about");

  return (
<div className="navigation__wrapper">
{/* <a href="/" className="navigation__logo">
<img src={logo}/>
  <h2>YOURNAME</h2></a> */}

<ul className="navigation__list">
<li onClick={()=>setActiveDot("about")}><a href="#about" data-scroll="about"><span>O Nas</span><div className="dot"/></a></li>
<li onClick={()=>setActiveDot("services")}><a href="#services" data-scroll="services"><span>Us≈Çugi</span><div className="dot"/></a></li>
<li onClick={()=>setActiveDot("realizations")}><a href="#realizations" data-scroll="realizations"><span>Realizacje</span><div className="dot-active"/></a></li>
<li onClick={()=>setActiveDot("contact")}><a href="#contact" data-scroll="contact"><span>Kontakt</span><div className="dot"/></a></li>
</ul>
</div> 

  );
}

export default Navigation;
