//     pinType: document.querySelector(".main__wrapper").style.transform ? "transform" : "fixed"
//   });


  // gsap.to("#contact", {
  //   "backgroundColor": "blue",
  //   scrollTrigger: {
  //     trigger: ".section-1",
  //    // scroller: "#contact",
  //     start: 'top 40%',
  //     //scrub: true,
  //     markers: true
